mapper.update:
   	update board_comment
   	   set deleted = 1
		 where commentNo=#{commentNo}

   	update board_comment
   	   set recommend_count = recommend_count + 1
		 where commentNo=#{commentNo}

   	update board_comment
   	   set recommend_count = recommend_count - 1
		 where commentNo=#{commentNo}

mapper.select:
    	select count(*)
    	  from board_comment
		 where boardNo = #{boardNo}
