mapper.update:
   	update board_comment
   	   set deleted = 1
		 where commentNo=#{commentNo}

   	update board_comment
   	   set recommend_count = recommend_count + 1
		 where commentNo=#{commentNo}

mapper.select:
    select recommend_count
	  from board_comment
	 where commentNo = #{commentNo}

    select recommend_notify_step
	  from board_comment
	 where commentNo = #{commentNo}

    select boardNo
	  from board_comment
	 where commentNo = #{commentNo}
